import React, { useState, useEffect } from 'react';
import Transaction from './Transaction';
import SearchBar from './SearchBar';
import AddTransactionForm from './AddTransactionForm';

function TransactionList(props) {
  const [transactions, setTransactions] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);

  useEffect(() => {
    fetch('http://localhost:8001/transactions')
      .then(response => response.json())
      .then(data => setTransactions(data))
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleAddTransaction = (transaction) => {
    fetch('http://localhost:8001/transactions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(transaction)
    })
      .then(response => response.json())
      .then(data => {
        setTransactions([...transactions, data]);
        setShowAddForm(false);
      })
      .catch(error => console.error(error));
  };

  const filteredTransactions = transactions.filter(transaction => {
    const nameMatches = transaction.name.toLowerCase().includes(searchQuery);
    const amountMatches = transaction.amount.toString().includes(searchQuery);
    return nameMatches || amountMatches;
  });

  return (
    <div>
      <h2>Recent Transactions</h2>
      <SearchBar onSearch={handleSearch} />
      {showAddForm ? (
        <AddTransactionForm onAddTransaction={handleAddTransaction} onCancel={() => setShowAddForm(false)} />
      ) : (
        <button onClick={() => setShowAddForm(true)}>Add Transaction</button>
      )}
      {filteredTransactions.map(transaction => (
        <Transaction key={transaction.id} transaction={transaction} />
      ))}
    </div>
  );
}

export default TransactionList;
