import React from 'react';

function TransactionTable(props) {
  const handleDelete = (id) => {
    fetch(`http://localhost:3000/transactions/${id}`, {
      method: 'DELETE'
    })
      .then(() => {
        props.onDelete(id);
      })
      .catch(error => console.log(error));
  };

  const filteredTransactions = props.transactions.filter(transaction => {
    const query = props.searchQuery.toLowerCase();
    return transaction.description.toLowerCase().includes(query) || transaction.category.toLowerCase().includes(query) || transaction.amount.toString().includes(query);
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Date</th>
          <th>Category</th>
          <th>Description</th>
          <th>Amount</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {filteredTransactions.map(transaction => (
          <tr key={transaction.id}>
            <td>{transaction.date}</td>
            <td>{transaction.category}</td>
            <td>{transaction.description}</td>
            <td>{transaction.amount}</td>
            <td><button onClick={() => handleDelete(transaction.id)}>Delete</button></td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default TransactionTable;
